import { useState } from 'react'

function App() {
  const [toDo, setToDo] = useState('')
  const [toDos, setToDos] = useState([])
  const onChange = (event) => setToDo(event.target.value)
  // console.log(toDo)
  const onSubmit = (event) => {
    event.preventDefault() //sbmit이 기본적으로 새로고침 기능이 있어서 없애주는것
    if (toDo === '') {
      //input값이 빈값이면 리턴
      return
    }
    setToDos((currentArray) => [toDo, ...currentArray]) //기존에 있는 배열에 추가 하는거 ...
    //food Array에 '5'라는 요소를 추가하고 싶음.
    // const food = [1,2,3,4]
    // [5, food] // 이런 식으로 요소를 추가한다면 아래와 같은 결과.
    // -> [5, food[]]
    // Array 안에 또 다른 Array가 중첩되는 결과.

    // 그래서 아래와 같이 추가하면 됨.
    // ★[5, ...food]
    // 앞에 [ ... ]을 추가하면 Array의 element 들을 돌려주게 됨.
    setToDo('') //서브밋을 하고 input을 빈값으로 만들기
  }
  // console.log(toDos)
  return (
    <div>
      <h1>My To Dos({toDos.length})</h1>
      <form onSubmit={onSubmit}>
        {/* form은 기본적으로 submit의 속성을 가지고 있다 */}
        <input
          onChange={onChange}
          value={toDo}
          type="text"
          placeholder="Write your To do ....."
        />
        {/* 폼을 쓰는 이유 form 안에 버튼을 넣으면 onClick을 사용하지 않아도 엔터/마우스 클릭시 똑같은 기능/효과를 사용할 수 있고, 인풋의 경우도 form이 제공하는 자체 validation(아무것도 입력하지 않으면 알려줌 등) 기능도 사용할 수 있는 이점 */}
        <button>Add To Do</button>
      </form>
    </div>
  )
}

export default App
